mapper.update:
UPDATE house_contract_score
SET place_score = #{placeScore}, contract_score = #{contractScore}
WHERE house_id = #{houseId}

mapper.select:
SELECT house_id "houseId", ssg_name "ssgName", dong_name "dongName", rent_kind "rentKind", rent_deposit "rentDeposit", rent_fee "rentFee"
FROM house_contract
WHERE acc_year = #{accYear} AND ssg_name = #{ssgName} AND dong_code = #{dongCode} AND house_kind_name = #{houseKindName}
ORDER BY house_id DESC
LIMIT #{param5}, #{param6}

SELECT count(house_id)
FROM house_contract
WHERE acc_year = #{accYear} AND ssg_name = #{ssgName} AND dong_code = #{dongCode} AND house_kind_name = #{houseKindName}
